mapper.select:
select file_id , word , referance_count , regist_datetime , modify_datetime from word
and file_id = #{fileId}
and word = #{word}
order by file_id, word limit #{start}, #{offset}

mapper.insert:
insert into word (
	file_id
	, word
	, referance_count
	, regist_datetime
	, modify_datetime
) values (
	#{fileId}
	, #{word}
	, #{referanceCount}
	, #{registDatetime}
	, #{modifyDatetime}
)

mapper.update:
update word set
	referance_count = #{referanceCount}
	, modify_datetime = #{modifyDatetime}
where file_id = #{fileId}
  and word = #{word}
