mapper.select:
select word , header , word_type , description , lank , difficulty , regist_datetime , modify_datetime from word
and word like '%' || #{word} || '%'
and header = #{header}
and word_type = #{wordType}
and lank = #{lank}
and difficulty = #{difficulty}
order by header limit #{start}, #{offset}

select count(*)
from word


		and word = #{word}


		and header = #{header}


		and word_type = #{wordType}


		and lank = #{lank}


		and difficulty = #{difficulty}

select word , header , word_type , description , lank , difficulty , regist_datetime , modify_datetime from word
and lank >= #{lank}
order by lank, difficulty limit #{start}, #{offset}

mapper.insert:
insert into word (
	word
	, header
	, word_type
	, description
	, lank
	, difficulty
	, regist_datetime
	, modify_datetime
) values (
	#{word}
	, #{header}
	, #{wordType}
	, #{description}
	, #{lank}
	, #{difficulty}
	, #{registDatetime}
	, #{modifyDatetime}
)

mapper.update:
update word
word = #{updateWord}, word_type = #{wordType}, description = #{description}, lank = #{lank}, difficulty = #{difficulty}, modify_datetime = #{modifyDatetime}
where word = #{word}
