mapper.select:
select
	word
	, header
	, lank
	, difficulty
	, regist_datetime
	, modify_datetime
from word


		and word = #{word}


		and header = #{header}


		and lank = #{lank}


		and difficulty = #{difficulty}


order by header, lank, difficulty
limit #{start}, #{offset}

mapper.insert:
insert into word (
	word
	, header
	, lank
	, difficulty
	, regist_datetime
	, modify_datetime
) values (
	#{word}
	, #{header}
	, #{lank}
	, #{difficulty}
	, #{registDatetime}
	, #{modifyDatetime}
)

mapper.update:
update word

	lank = #{lank},
	difficulty = #{difficulty},
	modify_datetime = #{modifyDatetime}

where word = #{word}
