mapper.select:
select
	work_day
	, user_id
	, memo
	, modify_datetime
from workbook


		and work_day = #{workDay}


		and user_id = #{userId}


order by work_day desc
limit #{start}, #{offset}

select count(*)
from workbook


		and work_day = #{workDay}


		and user_id = #{userId}

select work_day , user_id , memo , modify_datetime from workbook
and work_day = #{workDay}
and user_id = #{userId}

mapper.insert:
insert into workbook (
	work_day
	, user_id
	, memo
	, modify_datetime
) values (
	#{workDay}
	, #{userId}
	, #{memo}
	, #{modifyDatetime}
)

mapper.delete:
delete from workbook
where work_day = #{workDay}
and user_id = #{userId}

